import Request from"./request.js";async function login(){const e=document.querySelector(".enter-window__form"),t=e.querySelector('[type="email"]').value,a=e.querySelector('[type="password"]').value,s=await new Request("http://cards.danit.com.ua").post("/login",{email:t,password:a});if("Login or password are incorrect"===s.data.message)alert(s.data.message);else{const e=s.data.token;sessionStorage.setItem("token",e)}}const baseUrl=new Request("http://cards.danit.com.ua",{Authorization:"Bearer "+sessionStorage.getItem("token")});async function saveVisitOnBase(e){return(await baseUrl.post("/cards",e)).data}async function deleteCard(e){return await baseUrl.delete("/cards/"+e)}async function getAllCards(){return await baseUrl.get("/cards")}async function getCard(e){return await baseUrl.get("/cards/"+ +e)}async function editVisitOnBase(e,t){return await baseUrl.put("/cards/"+e,t)}export{login,saveVisitOnBase,getAllCards,getCard,deleteCard,editVisitOnBase};